import logo from './logo.svg';
import './App.css';


const StyledBackground = styled.div`
  background-image: url(${require('../assets/bg.jpg')});
  background-size: cover;
  background-position: center;
  height: 100%;
  width: 100%;
`;

const StyledText = styled.p`
  color: #fff;
  font-size: 18px;
  font-family: Arial, sans-serif;
  text-align: center;
  margin: 20px auto;
  max-width: 800px;
`;
function App() {
  return (
    <div className="App">

      <h1>Mẹ</h1>
      <p>
        Mẹ là tiếng hát câu thơ<br/>
        Ru con vào những giấc mơ tuyệt vời<br/>
        Du dương hai tiếng à ơi<br/>
        Đong đưa con ngủ, suốt đời không quên
      </p>
      <p>
        Mỗi ngày con lớn khôn lên<br/>
        Trong vòng tay mẹ êm đềm thiết tha<br/>
        Mẹ là tổ ấm mái nhà<br/>
        Tình thương, cao cả, bao la, dạt dào
      </p>
      <p>
        Lời mẹ dạy bảo ngọt ngào<br/>
        Hiện tử, âu yếm, xiết bao ân tình<br/>
        Mẹ là nắng ấm bình mình<br/>
        Là bông hoa cúc đẹp xình sáng ngời<br/>
      </p>
    </div>
  );
}

export default App;
