import logo from './logo.svg';
import './App.css';


const StyledBackground = styled.div`
  background-image: url(${require('../assets/bg.jpg')});
  background-size: cover;
  background-position: center;
  height: 100%;
  width: 100%;
`;

const StyledText = styled.p`
  color: #fff;
  font-size: 18px;
  font-family: Arial, sans-serif;
  text-align: center;
  margin: 20px auto;
  max-width: 800px;
`;
function App() {
  return (
    <div className="App">

      <h1>Mẹ</h1>
      <StyledBackground>
      <StyledText>
        Mẹ là tiếng hát câu thơ
        Ru con vào những giấc mơ tuyệt vời
        Du dương hai tiếng à ơi
        Đong đưa con ngủ, suốt đời không quên
      </StyledText>
      <StyledText>
        Mỗi ngày con lớn khôn lên
        Trong vòng tay mẹ êm đềm thiết tha
        Mẹ là tổ ấm mái nhà
        Tình thương, cao cả, bao la, dạt dào
      </StyledText>
      <StyledText>
        Lời mẹ dạy bảo ngọt ngào
        Hiện tử, âu yếm, xiết bao ân tình
        Mẹ là nắng ấm bình mình
        Là bông hoa cúc đẹp xình sáng ngời
      </StyledText>
    </StyledBackground>
    </div>
  );
}

export default App;
